module.exports = (api) => {
  api.cache(() => process.env.NODE_ENV === 'production');

  const isTest = api.env('test');

  return {
    presets: [
      ['@babel/preset-env', { targets: isTest ? { node: 'current' } : { esmodules: true } }],
      '@babel/preset-typescript',
      ['@babel/preset-react', { runtime: 'automatic' }],
    ],
  };
};
